/**
 * @usedBy ThfTableComponent
 *
 * @description
 *
 * Interface para configuração das colunas de labels do `thf-table`.
 */
export interface ThfTableColumnLabel {
    /**
     * Define a cor do label. Valor *default* é `primary`. Aceita os valores:
     *
     * <span class="dot thf-color-01"></span> `color-01` ou `primary`
     *
     * <span class="dot thf-color-02"></span> `color-02`
     *
     * <span class="dot thf-color-03"></span> `color-03`
     *
     * <span class="dot thf-color-04"></span> `color-04`
     *
     * <span class="dot thf-color-05"></span> `color-05`
     *
     * <span class="dot thf-color-06"></span> `color-06`
     *
     * <span class="dot thf-color-07"></span> `color-07` ou `danger`
     *
     * <span class="dot thf-color-08"></span> `color-08` ou `warning`
     *
     * <span class="dot thf-color-09"></span> `color-09`
     *
     * <span class="dot thf-color-10"></span> `color-10`
     *
     * <span class="dot thf-color-11"></span> `color-11` ou `success`
     *
     * <span class="dot thf-color-12"></span> `color-12`
     */
    color?: string;
    /** Texto de exibição. */
    label: string;
    /** Valor da legenda. */
    value: string;
}
